import React, { useState } from "react";

// function InputForm() {
//     return (
//         <div>
//             this is the form section
//         </div>
//     )
// }

const InputForm = () => {
  const [name, setName] = useState(""); //add all the elemetns later

  const handleSubmit = (e) => {
    e.preventDefault();
    const experience = { name }; //add all the form fields here

    fetch("//the url of the post goes here", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(experience),
    }).then(() => {
      console.log("uploaded to server succesfully");
      //what to do next....eg: history.push('/');
    });
  };

  return (
    <div className="FormInput">
      <h3>Form Title</h3>
      <form className="" onSubmit={handleSubmit}>
        <label className="">First label:</label>
        <input
          type="text"
          required
          value={name}
          onChange={(e) => setName(e.target.value)}
        />

      </form>
    </div>
  );
};
export default InputForm;
